let xpos = 50;
let ypos = 50;

let hello = "Hello World";

let d = "d";


function setup() {
createCanvas(1000, 1000);
background(220);
}



function draw() {

    xpos += xpos ;
    ypos = 3*ypos;
 
    text(hello + d, xpos, ypos);
    
}

